        console.log(''   Is verified:'', autoValidatedOnLeaderboard[0].is_verified);
      }

      // Check how many pass the filtering
      const { data: filteredAutoValidated } = await supabase
        .from(''season_leaderboard'')
        .select(''user_id, pick_source, is_verified'')
        .eq(''season'', 2025)
        .in(''user_id'', uniqueUsers)
        .or(''is_verified.eq.true,pick_source.eq.anonymous,pick_source.eq.mixed'');

      console.log(''âœ… Auto-validated users passing filter:'', filteredAutoValidated?.length || 0);

      const gap = (autoValidatedOnLeaderboard?.length || 0) - (filteredAutoValidated?.length || 0);
