      console.log(''Sample weekly entries:'');
      weeklyAll.slice(0, 5).forEach(user => {
        console.log(''  User:'', user.display_name, ''Source:'', user.pick_source, ''Rank:'', user.weekly_rank);
      });
    } else {
      console.log(''âŒ NO WEEKLY DATA FOUND - table may not be populated for 2025'');
